mapper.insert:
INSERT INTO DMP_SELECT.STAT_RESULT (STAT_TIME,STAT_TIME_SPAN, INDEX_NAME, INDEX_VALUE, INDEX_SOURCE, CREATE_TIME,UPDATE_TIME) SELECT t1.startDate,t1.timeSpan,t1.indexName,t1.activeUserNum,t1.userSource,t1.createTime,t1.updateTime FROM (
SELECT #{item.startDate,jdbcType=VARCHAR} startDate, #{item.timeSpan,jdbcType=VARCHAR} timeSpan, #{item.indexName,jdbcType=VARCHAR} indexName, #{item.activeUserNum,jdbcType=NUMERIC} activeUserNum, #{item.userSource,jdbcType=VARCHAR} userSource, sysdate createTime, sysdate updateTime FROM dual
) t1

mapper.select:
SELECT STAT_TIME statDate,INDEX_SOURCE userSource,INDEX_VALUE activeUserNum FROM DMP_SELECT.STAT_RESULT WHERE INDEX_NAME = 'activeNum' AND STAT_TIME_SPAN='D' AND
STAT_TIME >= #{startDate}
and STAT_TIME <= #{endDate}
ORDER BY INDEX_VALUE DESC,INDEX_SOURCE,STAT_TIME
