mapper.insert:
INSERT INTO DMP_SELECT.STAT_RESULT (STAT_TIME,STAT_TIME_SPAN, INDEX_NAME, INDEX_VALUE, INDEX_SOURCE, CREATE_TIME,UPDATE_TIME) SELECT t1.startDate,t1.timeSpan,t1.indexName,t1.accumulatedAmount,t1.branchName,t1.createTime,t1.updateTime FROM (
SELECT #{item.statDay,jdbcType=VARCHAR} startDate, #{item.timeSpan,jdbcType=VARCHAR} timeSpan, #{item.indexName,jdbcType=VARCHAR} indexName, #{item.accumulatedAmount,jdbcType=NUMERIC} accumulatedAmount, #{item.branchName,jdbcType=VARCHAR} branchName, sysdate createTime, sysdate updateTime FROM dual
) t1

mapper.select:
SELECT INDEX_SOURCE branchName, sum(INDEX_VALUE) accumulatedAmount FROM DMP_SELECT.STAT_RESULT WHERE INDEX_NAME='lifePremium' AND STAT_TIME_SPAN='D' AND
STAT_TIME >= #{startDate}
AND
STAT_TIME <= #{endDate}
GROUP BY INDEX_SOURCE ORDER BY sum(INDEX_VALUE) DESC
