mapper.select:
SELECT to_char(MAKEDATE,'yyyy-mm-dd') statDate,MOBLIEPHONE mobile,USERSOURCE indexSource FROM CHINALIFEEC.GE_USER_PERSONAL WHERE MOBLIEPHONE IS NOT NULL AND CHECKSTATUS IN ('2','3') AND MAKEDATE>=TO_DATE (concat(#{startDate},' 00:00:00'),'yyyy-mm-dd hh24:mi:ss')
AND MAKEDATE<=TO_DATE (concat(#{endDate},' 23:59:59'),'yyyy-mm-dd hh24:mi:ss')
AND USERSOURCE=#{userSource}

SELECT
DISTRIBUTE_NAME distributeName,SUM (INDEX_VALUE) indexValue
FROM DMP_SELECT.DISTRIBUTE_RESULT
WHERE INDEX_NAME = 'registerMobile' AND STAT_TIME_SPAN = 'D'
AND STAT_TIME>=#{startDate}

      AND STAT_TIME<=#{endDate}

AND DISTRIBUTE_TYPE = #{distributeType}

    AND INDEX_SOURCE IN ${userSource}

GROUP BY DISTRIBUTE_NAME ORDER BY SUM (INDEX_VALUE) desc

SELECT
DISTRIBUTE_NAME distributeName,SUM (INDEX_VALUE) indexValue
FROM DMP_SELECT.DISTRIBUTE_RESULT
WHERE INDEX_NAME = 'activeIP' AND STAT_TIME_SPAN = 'D'
AND STAT_TIME>=#{startDate}

      AND STAT_TIME<=#{endDate}

AND DISTRIBUTE_TYPE = #{distributeType}

    AND INDEX_SOURCE IN ${userSource}

GROUP BY DISTRIBUTE_NAME ORDER BY SUM (INDEX_VALUE) desc

SELECT
DISTRIBUTE_NAME distributeName,SUM (INDEX_VALUE) indexValue
FROM DMP_SELECT.DISTRIBUTE_RESULT
WHERE INDEX_NAME = 'activeHour' AND STAT_TIME_SPAN = 'D'
AND STAT_TIME>=#{startDate}

      AND STAT_TIME<=#{endDate}

AND DISTRIBUTE_TYPE = #{distributeType}

    AND INDEX_SOURCE IN ${userSource}

GROUP BY DISTRIBUTE_NAME ORDER BY DISTRIBUTE_NAME

SELECT
DISTRIBUTE_NAME distributeName,SUM (INDEX_VALUE) indexValue
FROM DMP_SELECT.DISTRIBUTE_RESULT
WHERE INDEX_NAME = 'userCollection' AND STAT_TIME_SPAN = 'D'
AND STAT_TIME>=#{startDate}

      AND STAT_TIME<=#{endDate}

AND DISTRIBUTE_TYPE = #{distributeType}

    AND INDEX_SOURCE IN ${userSource}

GROUP BY DISTRIBUTE_NAME ORDER BY SUM(INDEX_VALUE) DESC

mapper.insert:
INSERT INTO DMP_SELECT.DISTRIBUTE_RESULT (STAT_TIME,STAT_TIME_SPAN, INDEX_NAME, INDEX_SOURCE, DISTRIBUTE_TYPE, DISTRIBUTE_NAME,INDEX_VALUE,CREATE_TIME,UPDATE_TIME) SELECT t1.statDate,t1.statDateSpan,t1.indexName,t1.indexSource,t1.distributeType,t1.distributeName,t1.indexValue,t1.createTime,t1.updateTime FROM (
SELECT #{item.statDate,jdbcType=VARCHAR} statDate, #{item.statDateSpan,jdbcType=VARCHAR} statDateSpan, #{item.indexName,jdbcType=VARCHAR} indexName, #{item.indexSource,jdbcType=VARCHAR} indexSource, #{item.distributeType,jdbcType=VARCHAR} distributeType, #{item.distributeName,jdbcType=VARCHAR} distributeName, #{item.indexValue,jdbcType=NUMERIC} indexValue, sysdate createTime, sysdate updateTime FROM dual
) t1
