SELECT DISTRIBUTE_NAME disName,sum(INDEX_VALUE) indexValue FROM DMP_SELECT.FUPIN_DISTRIBUTE_RESULT WHERE
STAT_TIME >= #{startDate}
and STAT_TIME <= #{endDate}
AND DISTRIBUTE_TYPE='2' AND INDEX_NAME='fupinOrderIPInfo' GROUP BY DISTRIBUTE_NAME ORDER BY sum(index_value) DESC

SELECT DISTRIBUTE_NAME disName,sum(INDEX_VALUE) indexValue FROM DMP_SELECT.FUPIN_DISTRIBUTE_RESULT WHERE
STAT_TIME >= #{startDate}
and STAT_TIME <= #{endDate}
AND DISTRIBUTE_TYPE='1' AND INDEX_NAME='fupinOrderIPInfo' GROUP BY DISTRIBUTE_NAME ORDER BY sum(index_value) DESC

select CASE seller_area WHEN '内蒙乌兰察布' THEN '乌兰察布' WHEN '广西省天等' THEN '天等' WHEN '广西省龙州' THEN '龙州' WHEN '湖北省丹江口' THEN '丹江口' WHEN '湖北省郧西' THEN '郧西' ELSE '线下邮件未知地区' END area,count(DISTINCT seq) orderNum,sum(money_amount) orderAmount FROM offline_fupin WHERE status=0 AND
order_date >= '2017-12-29'
order_date >= #{startDate}
AND order_date <= #{endDate}
AND product_name NOT LIKE '%国寿丹泉%' GROUP BY seller_area

