                    and STAT_TIME <= #{endDate}
        
        AND STAT_TIME_SPAN='D' AND INDEX_NAME='fupinOrderFromToAreaInfo'
        
            AND SOURCE IN ${whereCondition}
        
        
            AND TARGET IN ${whereCondition1}
        
        GROUP BY SOURCE

        SELECT TARGET target,sum(INDEX_VALUE) indexValue
        FROM DMP_SELECT.FUPIN_FLOW_STAT
        WHERE
        
			        STAT_TIME >= #{startDate}
		        
        
                    and STAT_TIME <= #{endDate}
        
        AND STAT_TIME_SPAN='D' AND INDEX_NAME='fupinOrderFromToAreaInfo'
        
            AND SOURCE IN ${whereCondition}
        
        
            AND TARGET IN ${whereCondition1}
        
        GROUP BY TARGET

mapper.insert:
INSERT INTO DMP_SELECT.FUPIN_DISTRIBUTE_RESULT (STAT_TIME,STAT_TIME_SPAN, INDEX_NAME, DISTRIBUTE_TYPE, DISTRIBUTE_NAME,INDEX_VALUE,CREATE_TIME,UPDATE_TIME) SELECT t1.statDate,t1.statDateSpan,t1.indexName,t1.disType,t1.disName,t1.indexValue,t1.createTime,t1.updateTime FROM (
SELECT #{item.statDate,jdbcType=VARCHAR} statDate, #{item.statDateSpan,jdbcType=VARCHAR} statDateSpan, #{item.indexName,jdbcType=VARCHAR} indexName, #{item.disType,jdbcType=VARCHAR} disType, #{item.disName,jdbcType=VARCHAR} disName, #{item.indexValue,jdbcType=NUMERIC} indexValue, sysdate createTime, sysdate updateTime FROM dual
) t1

INSERT INTO DMP_SELECT.FUPIN_FLOW_STAT (STAT_TIME,STAT_TIME_SPAN, INDEX_NAME, SOURCE, TARGET,INDEX_VALUE,CREATE_TIME,UPDATE_TIME) SELECT t1.statDate,t1.statDateSpan,t1.indexName,t1.from1,t1.to1,t1.indexValue,t1.createTime,t1.updateTime FROM (
SELECT #{item.statDate,jdbcType=VARCHAR} statDate, #{item.statDateSpan,jdbcType=VARCHAR} statDateSpan, #{item.indexName,jdbcType=VARCHAR} indexName, #{item.from,jdbcType=VARCHAR} from1, #{item.to,jdbcType=VARCHAR} to1, #{item.indexValue,jdbcType=NUMERIC} indexValue, sysdate createTime, sysdate updateTime FROM dual
) t1
