mapper.insert:
INSERT INTO DMP_SELECT.STAT_RESULT (STAT_TIME,STAT_TIME_SPAN, INDEX_NAME, INDEX_VALUE, INDEX_SOURCE, CREATE_TIME,UPDATE_TIME) SELECT t1.registerDate,t1.timeSpan,t1.indexName,t1.registerUserNum,t1.userSource,t1.createTime,t1.updateTime FROM (
SELECT #{item.registerDate,jdbcType=VARCHAR} registerDate, #{item.timeSpan,jdbcType=VARCHAR} timeSpan, #{item.indexName,jdbcType=VARCHAR} indexName, #{item.registerUserNum,jdbcType=NUMERIC} registerUserNum, #{item.userSource,jdbcType=VARCHAR} userSource, sysdate createTime, sysdate updateTime FROM dual
) t1

mapper.select:
SELECT INDEX_SOURCE userSource,#{startDate} startDate,#{endDate} endDate,sum(INDEX_VALUE) registerUserNum FROM DMP_SELECT.STAT_RESULT WHERE INDEX_NAME = 'registerNum' AND
STAT_TIME >= #{startDate}
and STAT_TIME <= #{endDate}
GROUP BY INDEX_SOURCE ORDER BY registerUserNum DESC
